import { FiAirplay } from "react-icons/fi";
import { useParams } from "react-router-dom";
import array from '../data/postData.js';

function SinglePost() {
  console.log("SINGLE POST IS CALLED");

  const { id } = useParams();
  // let postNumber = props.match.params.id;
  // let postNumber = 1;
  let post = array[id];

  console.log("POST NUMBER = ", id);

  return (

    <div>
        <div id="post" >
          <h1>{post.name} <FiAirplay /></h1>
          <p id="date">{post.date}</p>
          <span className="contentWrap">         
            <img src={post.image} alt={"log"} id="img"/>
            <p id="content">{post.content}</p>
          </span>
        </div>
    </div>
  );  

}

export default SinglePost;
