import { FiAirplay } from "react-icons/fi";
import array from '../data/postData.js';

function SinglePost(props) {

  let postNumber = props.match.post.id;
  // let postNumber = 1;
  let post = array[postNumber];

  console.log(post);

  return (

    <div>
        <div id="post" >
          <h1>{post.name} <FiAirplay /></h1>
          <p id="date">{post.date}</p>
          <span className="contentWrap">         
            <img src={post.image} alt={"log"} id="img"/>
            <p id="content">{post.content}</p>
          </span>
        </div>
    </div>
  );  

}

export default SinglePost;
