import { FiAirplay } from "react-icons/fi";
import { Link } from "react-router-dom";
import React, { Component } from 'react';


class Post extends Component{

  render() {    
    return (
      <div>
        <Link to={`/blogs/${this.props.post.id}`}>
          <div id="post" >
            <h1>{this.props.post.name} <FiAirplay /></h1>
            <p id="date">{this.props.post.date}</p>
            <span className="contentWrap">         
              <img src={this.props.post.image} alt={"log"} id="img"/>
              <p id="content">{this.props.post.content}</p>
            </span>
          </div>
        </Link>
      </div>
    )  
  }

  componentDidMount() {
    fetch('http://localhost:5000/')
      .then(response => {
        response.json().then(data => {
          console.log(data);
      })
    })
  }

  
}


